<template>
  <div class="container1">
    <a-input-search class="search" :loading="searching" v-model:value="search" placeholder="搜索，共200款商品"
      @search="onSearch" />

    <div class="search_histories">
      <a-button size='small' v-for="(item, index) in searchHistories" :key="index">{{ item }}</a-button>
    </div>

    <a-list :grid="{ gutter: 16, xs: 1, sm: 2, md: 4, lg: 4, xl: 4, xxl: 4 }" :data-source="searchInfoList">
      <template #renderItem="{ item }">
        <a-list-item>
          <a-card hoverable @click="">
            <template #cover>
              <a-image :src="item.list_pic_url" :alt="item.name"
                fallback="data:image/png;base64," />
            </template>
            <template #actions>
              <a-card-meta>
                <template #title>
                  <span :title="item.name">{{ item.name }}</span>
                </template>
                <template #description>
                  <span>价格：</span>
                  <span class="description">{{ `R$${item.min_retail_price}` }}</span>
                </template>
              </a-card-meta>
            </template>
          </a-card>
        </a-list-item>
      </template>
    </a-list>
  </div>
</template>
  
<script setup lang='ts'>
// import { GoodInfo } from '@/model/CategoryModel';
import { useLocalStorage } from '@vueuse/core'
import { get } from '@/service/request'
import type { SearchGoodsList } from '@/model/Search'
const search = ref<string>("")
const searchHistories = useLocalStorage<string[]>('searchHistories', [])
const searchInfoList = ref<SearchGoodsList[]>([])
const searching = ref<boolean>(false)

const onSearch = async () => {
  if (/\S/.test(search.value)) {
    searchHistories.value = Array.from(new Set([...searchHistories.value, search.value]))
  }
  searching.value = true
  try {
    const res = await get('GoodsList', { keyword: search.value ?? undefined })

    if (res.errno === 0) {

      searchInfoList.value = res.data


    } else {

    }

  } catch (error) {

  } finally {
    searching.value = false
  }
}
</script>
  
<style scoped lang="scss">
.container1 {
  padding: 32px;
  display: flex;
  align-items: center;
  flex-direction: column;

  .search {
    height: 32px;
  }

  .search_histories {
    font-size: 12px;
    color: gray;
    display: flex;
    gap: 10px;
  }
}
</style>